package arrays;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		   // create an array
		   int[] age = {12, 4, 5};

		   // loop through the array
		   // using for loop
		   System.out.println("Using for Loop:");
		   for(int i = 0; i < age.length; i++) {
		     System.out.println(age[i]);
		   }
		 }
		}
