package arrays;

public class ForEachLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 // create an array
		   int[] age = {12, 4, 5};

		   // loop through the array
		   // using for loop
		   System.out.println("Using for-each Loop:");
		   for(int a : age) {
		     System.out.println(a);
		   }
		 }
		}
