package Homework1;

public class StringOperation1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="Swastika";
		System.out.println(s.toUpperCase());//SWASTIKA   
		System.out.println(s.toLowerCase());//swastika  
		System.out.println(s);//Swastika(no change in original)    

	}

}
