
//Display sum of n Natural Number
public class SumOfNatureNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int sum=0;
		int n=1000;
		
		//for loop
		for(int i=1;i<=n;++i) {
			//body inside for loop
			sum+=i; //sum=sum+1
		}
		System.out.println("Sum="+sum);

	}

}
